  margin-bottom: 18px;
  border-bottom: 2px solid #efeaea;
  font-weight: normal;
  font-size: 1.3em;
}
.main h3 {
  margin: 12px 0 8px;
  font-size: 1.2em;
  line-height: 1.2
}
.main h3::after {
  width: 2px;
  padding-left: 6px;
  color: #2175bc;
  content: '|';
}
.main code {
  padding: .2em .4em;
  border-radius: 3px;
  font-size: 90%;
  background: rgba(27, 31, 35, .05);
}
.main pre {
  display: block;
  padding: 1em;
  border: 2px solid #ddd;
  border-radius: 4px;
  overflow-x: auto;
  line-height: 1.6;
  background: #f8f8f8;
  -webkit-overflow-scrolling: touch
}
.main pre code {
  padding: 0;
  background: none;
}
.main img {
  max-width: 100%;
}
.main table {
  width: 100%;
  line-height: 1.5;
  text-align: center;
}
.main td,
  .main th {
  padding: 6px 12px;
  border: 1px solid #ddd;
}
@media screen and (max-width: 320px) {
.main iframe {
    max-width: 272px;
}
}
@media all and (min-width: 900px) {
.main {
    font-size: 16px;
}
}
