}
.title,
.extendedTitle {
  -webkit-box-flex: 1;
      -ms-flex: 1;
          flex: 1;
  margin-left: 10px;
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
}
.extendedTitle {
  color: rgba(255, 0, 0, .6);
}
