export const isOneChineseCharacter = val => !!String(val).match(/^[\u4e00-\u9fa5]$/)

// 检测字符长度为1-20位
export const isLengthElt20 = val => !!String(val).match(/^.{1,20}$/)

// 检测是否为6位数字
export const isSixDigits = val => !!String(val).match(/^\d{6}$/)

